if (import.meta.main) {
  const args = process.argv.slice(2);
  const command = args[0] || 'isolate';

  const isolator = new WorkspaceIsolator();

  try {
    switch (command) {
      case 'isolate':
      case 'create':
        await isolator.createAllIsolatedWorkspaces();
        break;

      default:
        console.log('Usage: bun workspace-isolator.ts [isolate|create]');
        console.log('  isolate - Create isolated workspace environments');
        console.log('  create  - Alias for isolate');
        process.exit(1);
    }
  } catch (error) {
    Logger.error(' Workspace isolation failed:', error);
    process.exit(1);
  }
}

